import React, { useRef, useState } from 'react';
import { View, Text, StyleSheet, Dimensions, TouchableOpacity } from 'react-native';

const { height } = Dimensions.get('window');

const PortfolioComponent = () => {
  const [selectedItems, setSelectedItems] = useState({});

  const portfolioData = [
    { symbol: 'L14', ownerPrice: '145.00', currentPrice: '150.00', sharesOwned: '50', gainLoss: '+5.75%' },
    { symbol: 'L13', ownerPrice: '123.00', currentPrice: '193.00', sharesOwned: '5000', gainLoss: '+50.75%' },
    { symbol: 'ASC', ownerPrice: '145.00', currentPrice: '150.00', sharesOwned: '50', gainLoss: '+5.75%' },
    { symbol: 'ASE', ownerPrice: '145.00', currentPrice: '150.00', sharesOwned: '50', gainLoss: '+5.75%' },
    { symbol: 'APPL', ownerPrice: '145.00', currentPrice: '150.00', sharesOwned: '50', gainLoss: '+5.75%' },
  ];

  const renderPortfolioItems = portfolioData.map((item, index) => (
    <React.Fragment key={index}>
      <TouchableOpacity
        style={styles.portfolioItem}
        onPress={() => {
          setSelectedItems((prevSelectedItems) => ({
            ...prevSelectedItems,
            [index]: !prevSelectedItems[index],
          }));
        }}
      >
        <Text style={styles.symbolText}>{item.symbol}</Text>
        <Text style={styles.sharesText}>{item.ownerPrice}</Text>
        <Text style={styles.priceText}>{item.currentPrice}</Text>
        <Text style={styles.sharesText}>{item.sharesOwned}</Text>
        <Text style={[styles.gainLossText, { color: item.gainLoss.startsWith('+') ? 'green' : 'red' }]}>
          {item.gainLoss}
        </Text>
      </TouchableOpacity>

      {/* Conditionally render the detail view below the selected item */}
      {selectedItems[index] && (
        <View style={styles.detailContainer}>
          <Text>{`Symbol: ${item.symbol}`}</Text>
          <Text>{`Owner Price: ${item.ownerPrice}`}</Text>
          <Text>{`Current Price: ${item.currentPrice}`}</Text>
          <Text>{`Shares Owned: ${item.sharesOwned}`}</Text>
          <Text style={{ color: item.gainLoss.startsWith('+') ? 'green' : 'red' }}>
            {`Gain/Loss: ${item.gainLoss}`}
          </Text>
        </View>
      )}
    </React.Fragment>
  ));

  return (
    <View style={styles.container}>
      {/* Handle area */}
      <View style={styles.panelHandle} />

      <View style={styles.tableHeader}>
        <Text style={styles.headerLabel}>MÃ CP</Text>
        <Text style={styles.headerLabel}>GIÁ VỐN</Text>
        <Text style={styles.headerLabel}>GIÁ TT</Text>
        <Text style={styles.headerLabel}>KL</Text>
        <Text style={styles.headerLabel}>LÃI/LỖ</Text>
      </View>

      {renderPortfolioItems}

      <View style={styles.whiteBox} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
  },
  panelHandle: {
    backgroundColor: 'red',
    width: '100%',
  },
  portfolioItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ECECEC',
  },
  symbolText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  sharesText: {
    fontSize: 18,
  },
  priceText: {
    fontSize: 18,
  },
  gainLossText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  tableHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    padding: 10,
    backgroundColor: '#f8f8f8',
    borderTopLeftRadius: 20,
    borderTopRightRadius: 20,
  },
  headerLabel: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  detailContainer: {
    backgroundColor: 'white',
    padding: 16,
    borderRadius: 8,
    zIndex: 1,
    elevation: 3,
  },
  whiteBox: {
    height: height * 0.05,
  },
});

export default PortfolioComponent;
