import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Dimensions, ImageBackground, Image, TouchableWithoutFeedback } from 'react-native';

const { height,width } = Dimensions.get('window');


const FinancialSummary = () => {
  const [isContentHidden, setContentHidden] = useState(false);

  const toggleContentHidden = () => {
    setContentHidden(prevState => !prevState);
  };

  return (
    <View style={styles.container}>
      <ImageBackground 
        source={require('../assets/finance_box.jpg')} 
        style={styles.absoluteFill} 
        resizeMode="stretch" 
      >
        <View style={styles.summary}>
          <View style={styles.detailColumn}>
            <View style={styles.firstLineBox}>
              <Text style={styles.title}>Tài sản ròng</Text>
              <TouchableWithoutFeedback onPress={toggleContentHidden} style={styles.eyeBox}>
                  <Image 
                    source={isContentHidden ? require('../assets/eye_true.jpg') : require('../assets/eye_false.jpg')} 
                    resizeMode='contain'
                    style={styles.eye}
                  />
              </TouchableWithoutFeedback>
            </View>
            <Text style={styles.amount}>7 VND</Text>
            <Text style={styles.subTitle}>Sức mua</Text>
            <Text style={styles.subAmount}>7 VND</Text>
          </View>
          <View style={styles.actionColumn}>
            <TouchableOpacity>
              <Image style={styles.detailButton} source={require("../assets/chitiet.jpg")} />
            </TouchableOpacity>
            <TouchableOpacity>
              <Image style={styles.statusButton} source={require('../assets/trangthai.jpg')} />
            </TouchableOpacity>
            <TouchableOpacity>
              <Image style={styles.saveRatioButton} source={require("../assets/save_ratio.jpg")}/>
            </TouchableOpacity>
          </View>
        </View>
      </ImageBackground>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: width,
    height: height*0.21,
  },
  absoluteFill: {
    width: width*1,
    height: height*0.205,

    position: "absolute",
    top: 0,
  },
  summary: {
    height: height*0.2,
    padding: 25,
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  firstLineBox: {
    flexDirection: "row",
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: height*-0.015,

  },
  detailColumn: {
    justifyContent: 'space-around',
  },
  actionColumn: {
    width: width*0.5,
    height:'auto',
    justifyContent: 'space-around',
    marginRight: width*0.02,
  },
  title: {
    fontSize: 13,
    fontWeight: 'normal',
    color: '#333',
  },
  eye: {
    marginLeft: 5,
    width: 18,
    height: 18,
  },
  amount: {
    fontSize: 18,
    fontWeight: '600',
    color: '#333',
  },
  subTitle: {
    fontSize: 13,
    fontWeight: 'normal',
    marginTop: height*0.03,
  },
  subAmount: {
    fontSize: 14,
    fontWeight: '500',
    color: '#333',
    marginTop: height*0.007,

  },
  detailButton: {
    position: "absolute",
    right: 0,
    width: width*0.15,
    height: height*0.02,

  },
  statusButton: {
    position: "absolute",
    right: 0,
    width: width*0.22,
    height: height*0.027,
    marginTop: height*0.013,
  },
  saveRatioButton: {
    position: "absolute",
    right: 0,
    width: width*0.4,
    height: height*0.02,
    marginTop: height*0.015,

  },
  buttonText: {
    fontSize: 14,
    fontWeight: 'bold',
    color: '#333',
  },
  status: {
    fontSize: 14,
    fontWeight: 'bold',
    color: 'green',
  },
  // ... Add other styles you might need
});

export default FinancialSummary;
