import React, { useState } from 'react';
import { View, Text, Switch, TouchableOpacity, StyleSheet, Dimensions } from 'react-native';
const { height,width } = Dimensions.get('window');

const PortfolioSummary = () => {
  // State for the toggle switch
  const [volumeHidden, setVolumeHidden] = useState(false);

  // Handler for when the toggle switch changes
  const toggleSwitch = () => setVolumeHidden(previousState => !previousState);

  // This is where you would fetch or pass real data
  const summaryData = {
    totalProfit: '+19,135,600',
    totalProfitPercent: '+1.51%',
    todayProfit: '-5,395,000',
    totalCapital: '1,266,209,400',
    marketValue: '1,285,345,000',
  };

  return (
    <View>
      <View style={styles.summaryContainer}>
        <View style={styles.summaryRow}>
          <Text style={styles.summaryTitle}>Lãi lỗ danh mục</Text>
          <Text style={[styles.summaryValue, styles.profitValue]}>{summaryData.totalProfit}</Text>
          <Text style={[styles.summaryPercentage, styles.profitPercentage]}>{summaryData.totalProfitPercent}</Text>
        </View>
        <View style={styles.divider} />
        <View style={styles.summaryRow}>
          <Text style={styles.summaryTitle}>Lãi/Lỗ hôm nay</Text>
          <Text style={[styles.summaryValue, styles.lossValue]}>{summaryData.todayProfit}</Text>
        </View>
        <View style={styles.summaryRow}>
          <Text style={styles.summaryTitle}>Tổng vốn</Text>
          <Text style={styles.summaryValue}>{summaryData.totalCapital}</Text>
        </View>
        <View style={styles.summaryRow}>
          <Text style={styles.summaryTitle}>Tổng giá trị thị trường</Text>
          <Text style={styles.summaryValue}>{summaryData.marketValue}</Text>
        </View>

      </View>
      <View style={styles.row}>
        <View style={styles.volumeRow}>
          <Switch
            trackColor={{ false: '#767577', true: '#81b0ff' }}
            thumbColor={volumeHidden ? '#f5dd4b' : '#f4f3f4'}
            onValueChange={toggleSwitch}
            value={volumeHidden}
          />
          <Text style={styles.volumeText}>Ẩn Khối Lượng</Text>
        </View>
        <TouchableOpacity style={styles.sellButton}>
          <Text style={styles.sellButtonText}>Bán nhiều mã</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  summaryContainer: {
    backgroundColor: 'lightgray',
    padding: 16,
    marginTop: 16,
    borderRadius: 8,
  },
  summaryRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 8,
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 8,
  },
  summaryTitle: {
    fontSize: 16,
    color: '#333',
  },
  summaryValue: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
  },
  summaryPercentage: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  profitValue: {
    color: 'green',
  },
  profitPercentage: {
    color: 'green',
  },
  lossValue: {
    color: 'red',
  },
  divider: {
    borderBottomColor: '#ECECEC',
    borderBottomWidth: 1,
    marginVertical: 8,
  },
  volumeRow: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingVertical: 8,
  },
  volumeText: {
    marginLeft: 8,
    fontSize: 16,
    color: '#333',
  },
  sellButton: {
    marginTop: 8,
    paddingVertical: 8,
    paddingHorizontal: 16,
    borderRadius: 20,
    borderWidth: 1,
    borderColor: 'red',
    alignSelf: 'flex-start', // Align to the left
  },
  sellButtonText: {
    color: 'red',
    fontSize: 16,
  },
});

export default PortfolioSummary;
